INSERT INTO `yemekler` (`id`, `yemek_adi`, `yemek_resmi`, `yemek_aciklama`, `yemek_bolge`, `yemek_il`, `yemek_link`) VALUES
(1, 'Yüksük Çorbası', 'yuksuk-corbasi.jpg', 'İçerisinde köfte, bulgur, nohut ve çeşitli baharatlar bulunan geleneksel bir çorba.', 'Akdeniz', 'Adana', 'yuksuk-corbasi-adana'),
(2, 'Tirşik Çorbası', 'tirşik-çorbası.jpg', 'Kurutulmuş yoğurt, bulgur ve baharatlarla yapılan doyurucu bir çorba. Genellikle tereyağı ve pul biberle servis edilir.', 'Akdeniz', 'Adana', 'tirsik-corbasi-adana'),
(3, 'Şırdan Dolması', 'şırdan-dolması.jpg', 'İçerisinde pirinç, baharatlar, bazen de kıyma bulunan kuzu bağırsağıyla yapılan bir özel yemek.', 'Akdeniz', 'Adana', 'sirdan-dolmasi-adana'),
(4, 'Mumbar', 'mumbar.jpg', 'Kuzu bağırsağıyla yapılan bir başka yemek. Pirinç, soğan, baharatlar ve yeşilliklerle birlikte pişirilir.', 'Akdeniz', 'Adana', 'mumbar-adana'),
(5, 'Para Para Çorbası', 'para-para-çorbası.jpg', 'Koyun veya sığır eti, sebzeler, domates salçası ve çeşitli baharatlarla yapılan lezzetli ve yoğun bir çorba.', 'Akdeniz', 'Adana', 'para-para-corbasi-adana'),
(6, 'Çiğköfte', 'çiğköfte.jpg', 'İnce bulgur, baharatlar, domates salçası ve çiğ kıyma veya cevizle yoğrulan popüler bir yemek. Genellikle marul yapraklarıyla servis edilir.', 'Güneydoğu Anadolu', 'Adıyaman', 'cigkofte-adiyaman'),
(7, 'Besni Tavası', 'besni-tavası.jpg', ' Küp doğranmış et (genellikle kuzu veya dana), soğan, biber, domates ve çeşitli baharatlarla birlikte tavada pişirilen lezzetli bir yemek.', 'Güneydoğu Anadolu', 'Adıyaman', 'besni-tavasi-adiyaman'),
(8, 'Buhara Pilavı', 'buhara-pilavı.jpg', ' Et, kuru meyveler (kayısı veya kuru üzüm gibi), kuruyemişler ve aromatik baharatlarla birlikte pişirilen lezzetli bir pilav.', 'Güneydoğu Anadolu', 'Adıyaman', 'buhara-pilavi-adiyaman'),
(9, 'İçli Köfte', 'içli-köfte.jpg', 'İnce bulgur kabuklarıyla kaplanmış, kıyma, soğan, baharatlar ve yeşilliklerle doldurulan kızartılmış bir yemek.', 'Güneydoğu Anadolu', 'Adıyaman', 'icli-kofte-adiyaman'),
(10, 'Kavurmalı Hıtap', 'kavurmalı-hıtap.jpg', ' Dilimlenmiş et (genellikle kuzu veya dana), soğan, biber ve domatesin tavada pişirilmesiyle hazırlanan bir yemek.', 'Güneydoğu Anadolu', 'Adıyaman', 'kavurmali-hitap-adiyaman'),
(11, 'Bütüm Et', 'bütüm-et.jpg', 'Bütün kuzu veya koyun etinin pişirildiği ve çeşitli yan yemeklerle servis edildiği bir yemek.', 'Ege', 'Afyon', 'besni-tavası-afyon'),
(12, 'Afyon Kebabı', 'afyon-kebabı.jpg', 'Baharatlarla marine edilen ve mükemmelliğe kadar ızgara edilen kuzu veya dana etinden yapılan bir bölgesel kebap çeşidi.', 'Ege', 'Afyon', 'afyon-kebabi-afyon'),
(13, 'Dana Tandır', 'dana-tandır.jpg', 'Baharatlar ve otlarla tatlandırılan, yavaş pişirilen rosto dana eti. Sonuç olarak, etin yumuşak ve lezzetli olması sağlanır.', 'Ege', 'Afyon', 'dana-tandir-afyon'),
(14, 'Dana Kavurma', 'dana-kavurma.jpg', ' Küp doğranmış ve baharatlanmış dana veya sığır etinin karamelize olana kadar pişirildiği bir yemek.', 'Ege', 'Afyon', 'dana-kavurma-afyon'),
(15, 'Paça', 'paça.jpg', ' İnek veya koyun paçasının haşlanmasıyla elde edilen bir çorba. Genellikle kahvaltıda veya akşam yemeği olarak tüketilir.', 'Ege', 'Afyon', 'paça-afyon'),
(16, 'Abdigor Köftesi', 'abdigor-köftesi.jpg', 'Kıyma, soğan, baharatlar ve diğer malzemelerle hazırlanan ve genellikle közde pişirilen köfte.', 'Doğu Anadolu', 'Agrı', 'abdigor-koftesi-agri'),
(17, 'Gösteberg Et', 'gösteberg-et.jpg', 'Ağrıya özgü bir et yemeği. Baharatlarla marine edilen ve ızgarada pişirilen et dilimlerinden oluşur', 'Doğu Anadolu', 'Ağrı', 'gosteberg-et-agri'),
(18, 'Haşıl', 'haşıl.jpg', 'Buğday, et suyu, tereyağı ve baharatlarla yapılan bir çorba veya yemek.', 'Doğu Anadolu', 'Ağrı', 'haşil-ağri'),
(19, 'Hengel', 'hengel.jpg', 'Taze ve kuru fasulye, et, soğan, biber ve baharatlarla hazırlanan bir yemek.', 'Doğu Anadolu', 'Ağrı', 'hengel-agri'),
(20, 'Erişte', 'erişte.jpg', ' Yufka hamurundan yapılan ve genellikle et veya sebzelerle birlikte tüketilen ince şerit şeklinde bir makarna türü.', 'Doğu Anadolu', 'Ağrı', 'erişte-agri'),
(21, 'Bakla Dolması', 'bakla-dolması.jpg', ' Bakla, pirinç, soğan, baharatlar ve diğer malzemelerle doldurulmuş ve pişirilmiş bir dolma.', 'Karadeniz', 'Amasya', 'bakla-dolmasi-amasya'),
(22, 'Ciğer Sarması', 'ciğer-sarması.jpg', ' İnce dilimlenmiş dana ciğeri, soğan, baharatlar ve sarımsakla sarılarak yapılan bir yemek.', 'Karadeniz', 'Amasya', 'ciger-sarmasi-amasya'),
(23, 'Madımak', 'madımak.jpg', 'Amasyaya özgü bir bitki olan madımak otu ile yapılan yemek. Genellikle soğan, yağ ve baharatlarla birlikte pişirilir.', 'Karadeniz', 'Amasya', 'madimak-amasya'),
(24, 'Pastırma Pağallemesi', 'pastırma-pağallemesi.jpg', 'Amasyanın ünlü pastırması, peynir, biber ve diğer malzemelerle birleştirilerek hazırlanan bir lezzetli atıştırmalık.', 'Karadeniz', 'Amasya', 'pastirma-pagallemesi-amasya'),
(25, 'Pirpirim', 'pirpirim.jpg', 'Kıyma, soğan, baharatlar ve ekmek kırıntısı ile hazırlanan, üzerine sos gezdirilerek pişirilen bir yemek.', 'Karadeniz', 'Amasya', 'pirpirim-amasya'),
(26, 'Ankara Tavası', 'ankara-tavasi.jpg', 'Dövülmüş et, patates, biber, domates ve baharatların bir araya gelerek tavada kızartıldığı bir yemek.', 'İç Anadolu', 'Ankara', 'ankara-tavasi-ankara'),
(27, 'Öllüğün Körü', 'öllüğün-körü.jpg', ' Biber salçası, soğan, domates ve baharatlarla pişirilen, içerisinde kuzu eti bulunan bir yemek.', 'İç Anadolu', 'Ankara', 'ollugun-koru-ankara'),
(28, 'Ankara Kebabı', 'ankara-kebabi.jpg', 'Kıyma, soğan, baharatlar ve ekmek kırıntısıyla yapılan, şişe geçirilerek mangalda pişirilen bir kebap çeşidi.', 'İç Anadolu', 'Ankara', 'ankara-kebabi-ankara'),
(29, 'İnceğiz Çorbası', 'incegiz-corbasi.jpg', 'Yayık çorbası olarak da bilinen, ince bulgur, yoğurt ve nane ile hazırlanan bir çorba.', 'İç Anadolu', 'Ankara', 'incegiz-corbasi-ankara'),
(30, 'Tamtak Tiridi', 'tamtak-tiridi.jpg', ' İnce bulgur, taze soğan, maydanoz, limon suyu, nar ekşisi ve zeytinyağı ile yapılan bir salata. Genellikle kebaplarla birlikte servis edilir. ', 'İç Anadolu', 'Ankara', 'tamtak-tiridi-ankara'),
(31, 'Domates Civesi', 'domates-civesi.jpg', 'Taze domates, soğan, biber ve baharatlarla hazırlanan, genellikle kahvaltılarda veya yan yemek olarak tüketilen bir yemek.', 'Akdeniz', 'Antalya', 'domates-civesi-antalya'),
(32, 'Ekşi Tarhana Yapımı ve Çorbası', 'eksi-tarhana-yapimi-ve-corbasi.jpg', 'Tarhana hamurunun hazırlanması ve kurutulmasıyla elde edilen, ardından sıcak su veya et suyuyla çorba haline getirilen ekşi bir çorba.', 'Akdeniz', 'Antalya', 'eksi-tarhana-yapimi-ve-corbasi-antalya'),
(33, 'Filisli Lapa', 'filisli-lapa.jpg', 'Yufka parçalarının sıvıya batırılıp, tereyağında kızartıldıktan sonra şerbetle tatlandırılan bir tatlı.', 'Akdeniz', 'Antalya', 'filisli-lapa-antalya'),
(34, 'Arap Kadayıfı', 'arap-kadayifi.jpg', 'Tel kadayıfın üzerine ceviz veya fındık serpilerek hazırlanan ve şerbetle tatlandırılan bir tatlı.', 'Akdeniz', 'Antalya', 'arap-kadayifi-antalya'),
(35, 'Antalya Piyazı', 'antalya-piyazi.jpg', 'Kuru fasulye, soğan, maydanoz, limon suyu ve zeytinyağından oluşan, yöresel bir salata veya meze.', 'Akdeniz', 'Antalya', 'antalya-piyazi-antalya'),
(36, 'Kaygana', 'kaygana.jpg', 'Yumurta, un ve süt ile hazırlanan, tavada kızartılan bir omlet çeşidi.', 'Karadeniz', 'Artvin', 'kaygana-artvin'),
(37, 'Kalaco', 'kalaco.jpg', 'Yöresel bir hamur işi olan Kalaco, un, su, tuz ve süt ile hazırlanan ve kızartılan bir tür börek.', 'Karadeniz', 'Artvin', 'kalaco-artvin'),
(38, 'Yoğurtlu Silor', 'yogurtlu-silor.jpg', ' Mısır unu, yoğurt, tereyağı ve baharatlarla yapılan, üzerine tereyağlı sos ve yoğurt gezdirilerek servis edilen bir yemek.', 'Karadeniz', 'Artvin', 'yogurtlu-silor-artvin'),
(39, 'Dede Cağ Kebap', 'dede-cag-kebap.jpg', 'Döş etinin şişe geçirilerek ateşte pişirildiği ve üzerine tereyağı gezdirilerek servis edilen bir kebap çeşidi.', 'Karadeniz', 'Artvin', 'dede-cag-kebap-artvin'),
(40, 'Aspirin Pide', 'aspirin-pide.jpg', ' Yumurtalı ve peynirli bir pide çeşidi.', 'Karadeniz', 'Artvin', 'aspirin-pide-artvin'),
(41, 'Börülce', 'börülce.jpg', 'Taze fasulye türü olan börülce, soğan, domates ve baharatlarla birlikte pişirilen bir yemek.', 'Ege', 'Aydın', 'börülce-aydin'),
(42, 'Yuvarlama', 'yuvarlama.jpg', ' Ispanak, pirinç, soğan ve baharatlarla doldurulan yaprak sarması.', 'Ege', 'Aydın', 'yuvarlama-aydin'),
(43, 'Çingene Pilavı', 'çingene-pilavı.jpg', 'Domates, biber, soğan, pirinç ve baharatlarla hazırlanan, renkli ve lezzetli bir pilav.', 'Ege', 'Aydın', 'cingene-pilavi-aydin'),
(44, 'Zerde', 'zerde.jpg', 'Pirinç, şeker, safran ve sütün bir araya gelerek hazırlanan, üzerine tarçın ve dövülmüş fındık serpilen tatlı bir pilav.', 'Ege', 'Aydın', 'zerde-aydin'),
(45, 'Pelvize Tatlısı', 'pelvize-tatlısı.jpg', 'Şeker, su ve nişastanın kaynatılarak elde edilen pelvisin üzerine fındık veya ceviz serpilerek hazırlanan bir tatlı.', 'Ege', 'Aydın', 'pelvize-tatlisi-aydin'),
(46, 'Acı Filiz', 'acı-filiz.jpg', 'Yöresel bir bitki olan acı filizin yapraklarından yapılan bir yemek. Yenilen filizler genellikle salata veya zeytinyağlı yemek olarak tüketilir.', 'Marmara', 'Balıkesir', 'aci-filiz-balikesir'),
(47, 'Arpa Şehriye Çorbası', 'arpa-şehriye-çorbası.jpg', 'Arpa şehriye, et suyu veya sebze suyu ile birlikte pişirilerek yapılan bir çorba.', 'Marmara', 'Balıkesir', 'arpa-sehriye-corbasi-balikesir'),
(48, 'Akkız', 'akkız.jpg', 'Mısır unu, tereyağı ve şekerle yapılan, üzerine tarçın serpilerek servis edilen bir tatlı.', 'Marmara', 'Balıkesir', 'akkiz-balikesir'),
(49, 'Balıkesir Mantısı', 'balıkesir-mantısı.jpg', 'İnce hamurun içerisine kıyma veya peynirle doldurularak yapılan ve üzerine yoğurt ve sos gezdirilerek servis edilen bir yemek.', 'Marmara', 'Balıkesir', 'balikesir-mantisi-balikesir'),
(50, 'Balıkesir Kaymaklısı', 'balıkesir-kaymaklısı.jpg', ' Yöreye özgü bir tatlı olan kaymaklısı, irmik, şeker ve süt ile yapılan bir sütlü tatlı.', 'Marmara', 'Balıkesir', 'balikesir-kaymaklisi-balikesir'),
(51, 'Ovmaç Çorbası', 'ovmaç-çorbası.jpg', 'Un, yoğurt ve suyla hazırlanan, üzerine tereyağlı sos gezdirilen bir çorba.', 'Marmara', 'Bilecik', 'ovmac-corbasi-bilecik'),
(52, 'Bıldırcın Kebabı', 'bıldırcın-kebabı.jpg', ' Bıldırcın etinin baharatlarla marine edilerek mangalda veya fırında pişirildiği bir kebap çeşidi.', 'Marmara', 'Bilecik', 'bildircin-kebabi-bilecik'),
(53, 'Nişasta Helvası', 'nişasta-helvası.jpg', 'Nişasta, tereyağı, şeker ve suyun kavrularak hazırlanan, üzerine dövülmüş ceviz serpilen bir tatlı.', 'Marmara', 'Bilecik', 'nisasta-helvasi-bilecik'),
(54, 'Zırz', 'zırz.jpg', 'Yufka parçalarının tereyağında kızartıldıktan sonra şerbetle tatlandırılan bir tatlı.', 'Marmara', 'Bilecik', 'zirz-bilecik'),
(55, 'Huris Tepsi', 'huris-tepsi.jpg', 'Hamurun üzerine peynir ve patates dilimleri eklenerek fırında pişirilen bir yemek.', 'Marmara', 'Bilecik', 'huris-tepsi-bilecik'),
(56, 'Tutmaç Çorbası', 'tutmaç-çorbası.jpg', 'Tel şehriye, et suyu veya tavuk suyu ile birlikte pişirilerek yapılan bir çorba.', 'Doğu Anadolu', 'Bingöl', 'tutmac-corbasi-bingol'),
(57, 'Mastuva', 'mastuva.jpg', 'Un, tereyağı ve su ile yapılan ve üzerine şeker serpilen bir tatlı.', 'Doğu Anadolu', 'Bingöl', 'mastuva-bingol'),
(58, 'Löl', 'löl.jpg', 'Bulgur, kıyma ve baharatlarla hazırlanan bir yemek.', 'Doğu Anadolu', 'Bingöl', 'lol-bingol'),
(59, 'Sorina Pel', 'sorina-pel.jpg', 'Yerel bir yemek olan sorina pel, nohut, bulgur, biber salçası ve baharatlarla yapılan bir çorba.', 'Doğu Anadolu', 'Bingöl', 'sorina-pel-bingol'),
(60, 'Keldoş', 'keldoş.jpg', 'Hamurun içerisine peynir veya patates konularak yapılan bir börek çeşidi.', 'Doğu Anadolu', 'Bingöl', 'keldos-bingöl'),
(61, 'Kaburga Dolması', 'kaburga-dolması.jpg', ' Kaburga etinin çeşitli malzemelerle doldurulup fırında pişirildiği bir yemek.', 'Doğu Anadolu', 'Bingöl', 'kaburga-dolmasi-bingol'),
(62, 'Şekalok Yemeği', 'şekalok-yemeği.jpg', 'Lahana, nohut, et ve baharatlarla hazırlanan bir yemek.', 'Doğu Anadolu', 'Bitlis', 'sekalok-yemegi-bitlis'),
(63, 'Çorti Köftesi', 'çorti-köftesi.jpg', 'Bulgur, kıyma, soğan ve baharatlarla yapılan bir köfte çeşidi.', 'Doğu Anadolu', 'Bitlis', 'corti-koftesi-bitlis'),
(64, 'Büryan Kebabı', 'büryan-kebabı.jpg', 'Kuzu veya oğlak etinin özel baharatlarla marine edildikten sonra tandırda pişirildiği bir kebap çeşidi.', 'Doğu Anadolu', 'Bitlis', 'buryan-kebabi-bitlis'),
(65, 'Katıklı Dolma', 'katıklı-dolma.jpg', 'Lahana yapraklarına doldurulan iç harcı ile hazırlanan bir dolma çeşidi.', 'Doğu Anadolu', 'Bitlis', 'katikli-dolma-bitlis'),
(66, 'Çireş Pancarı', 'çireş-pancarı.jpg', 'Yöresel bir sebze olan çireş pancarı, genellikle salata olarak tüketilir.', 'Doğu Anadolu', 'Bitlis', 'cires-pancari-bitlis'),
(67, 'Keledoş', 'keledoş.jpg', ' Un, tereyağı, su ve baharatlarla yapılan ve üzerine tereyağlı sos gezdirilen bir tatlı.', 'Doğu Anadolu', 'Bitlis', 'keledos-bitlis'),
(68, 'Kuymak', 'kuymak.jpg', ' Mısır unu, tereyağı ve peynirin karıştırılarak yapılan, koyu bir kıvama sahip bir yemek.', 'Marmara', 'Bolu', 'kuymak-bolu'),
(69, 'Ayva Dolması', 'ayva-dolması.jpg', 'Ayva meyvesinin çekirdek kısmının oyulup içerisine ceviz ve şekerli iç harcının doldurulmasıyla hazırlanan bir tatlı.', 'Marmara', 'Bolu', 'ayva-dolmasi-bolu'),
(70, 'Tas Kebabı', 'tas-kebabı.jpg', ' Kuzu eti, sebzeler ve baharatlarla birlikte tas içerisinde pişirilen bir kebap çeşidi.', 'Marmara', 'Bolu', 'tas-kebabi-bolu'),
(71, 'Cizleme Pilavı', 'cizleme-pilavı.jpg', 'Pilavın alt kısmının kısık ateşte kızartılmasıyla elde edilen bir pilav çeşidi.', 'Marmara', 'Bolu', 'cizleme-pilavi-bolu'),
(72, 'Laz Böreği', 'laz-böreği.jpg', 'Yufka, peynir ve tereyağı ile yapılan bir börek çeşidi.', 'Marmara', 'Bolu', 'laz-boreği-bolu'),
(73, 'Sisili Pilavı', 'sisili-pilavi.jpg', 'Pirinç, kuş üzümü, badem ve baharatlarla hazırlanan bir pilav çeşidi.', 'Akdeniz', 'Burdur', 'sisili-pilavi-burdur'),
